import React, { useState } from "react";
import {Product} from "./product";
import productData from "./productData";
// import {AddPage} from "./addPage";

export const Body = () =>{
    const productModal = productData.map(
        item => {
            return (

                <Product
                    key = {item.id}
                    list = {item}
                />

            )
        }
    )

    let [pop, setPop] = useState("true")
    const Page = () =>{
        setPop((element) => element = !element)
        console.log(pop)
    }

    return (
        <div className="body">
            <div className="add" onClick={Page}>新增产品</div>
            {pop ? null : {AddPage} }
            {productModal}
        </div>
    )
}
export const AddPage = () => {
    const [pop, setPop] = useState("true")
    const PageOff = () =>{
        setPop("false")

        console.log(pop)
    }
    return (
        <div className="addPage">
            <div className="head">Add product</div>
            <div className="textBlock">
                <p>avatar url : <input type="text"/></p>
                <p>product name : <input type="text"/></p>
                <p>desc : <input type="text"/></p>
            </div>
            <div className="bottom">
                <button onClick={PageOff}>Cancel</button>
                <button onClick={PageOff}>Ok</button>
            </div>
        </div>
    )
}
