import React, { useState } from "react";
import {Product} from "./product";
import productData from "./productData";
//import {render} from "react-dom";
//import {AddPage} from "./addPage";


export const Body = () => {
    const [showAddPage, setShowAddPage] = useState(false);
    const [product,setProduct] = useState(productData);
    const [url,setUrl] = useState("");
    const [name,setName] = useState("");
    const [introduction,setIntroduction] = useState("");
    const handleClick = () =>{
        setProduct(current =>[...current,{id:`${productData.length+1}`,url:"",name:"",introduction:""}])
    }

    const productModal = product.map(
        item => {
            return (

                <Product
                    key={item.id}
                    list={item}
                />


            )
        }
    )
    const saveFormData = (type) => {
        return (e) => {
            console.log(type, e.target.value);
            this.setState({
                [type]: e.target.value
            })
        }
    }


    const  handleSubmit = (e) => {
        e.preventDefault();
        const { username, password } = this;
        alert('avatar url ：' + this.state.url + 'product name ：' + this.state.name + 'desc :' + this.state.introduction)
    }
    return (
        <div className="body">
            <div className="add" onClick={() => {
                setShowAddPage(true)
            }}>新增产品
            </div>
            {showAddPage ?
                <div className="addPage">
                    <div className="head">Add product</div>
                    <form onSubmit={this.handleSubmit}>
                        <div className="textBlock">
                            <p>avatar url : <input
                                type="text"
                                name="url"
                                value= {url}
                                onChange={this.saveFormData("url")}
                            /></p>
                            <p>product name : <input
                                type="text"
                                name="name"
                                onChange={this.saveFormData("name")}
                            />
                            </p>
                            <p>desc : <input
                                type="text"
                                name="introduction"
                                onChange={this.saveFormData("introduction")}
                            /></p>
                        </div>
                        <div className="bottom">
                            <button onClick={() => setShowAddPage(false)}>Cancel</button>
                            <button
                                onClick={() => {
                                    setShowAddPage(false);
                                    handleClick();
                                }}>Ok
                            </button>
                        </div>
                    </form>
                </div>
                : null}
            {productModal}
        </div>
    )
}
