import React, { useState } from "react";
import {Product} from "./product";
import productData from "./productData";
import {render} from "react-dom";
//import {AddPage} from "./addPage";


export const Body = () => {
    const productModal = productData.map(
        item => {
            return (

                <Product
                    key={item.id}
                    list={item}
                />


            )
        }
    )
    const [showAddPage, setShowAddPage] = useState(false);
    // let saveFormData;
    // saveFormData = (type) =>{
    //     return (e) =>{
    //         console.log(type,e.target.value);
    //         this.setState(
    //             {
    //                 [type]: e.target.value
    //             }
    //         )
    //     }
    class MyComponent extends React.Component{
        state = {
            url :"",
            name :"",
            introduction :""
        }

        render()
    {
        return (
            <div className="body">
                <div className="add" onClick={() => {
                    setShowAddPage(true)
                }}>新增产品
                </div>
                {showAddPage ?
                    <div className="addPage">
                        <div className="head">Add product</div>
                        <form onSubmit={this.handleSubmit}>
                            <div className="textBlock">
                                <p>avatar url : <input type="text" name="url" onChange={this.saveFormData("url")}/></p>
                                <p>product name : <input type="text" name="name" onChange={this.saveFormData("name")}/>
                                </p>
                                <p>desc : <input type="text" name="introduction"
                                                 onChange={this.saveFormData("introduction")}/></p>
                            </div>
                            <div className="bottom">
                                <button onClick={() => setShowAddPage(false)}>Cancel</button>
                                <button onClick={() => {
                                    setShowAddPage(false)
                                }}>Ok
                                </button>
                            </div>
                        </form>
                    </div>
                    : null}
                {productModal}
            </div>
        )
    }

}
}
