import React, { useState } from "react";
import {Product} from "./product";
import productData from "./productData";
//import {AddPage} from "./addPage";
// import { createRoot } from 'react-dom/client';
// import { Container, Button, Alert } from 'react-bootstrap';
// import { CSSTransition } from 'react-transition-group';

export const Body = () =>{
    const productModal = productData.map(
        item => {
            return (

                <Product
                    key = {item.id}
                    list = {item}
                />


            )
        }
    )
    const [showAdded,setShowAdded]=useState(true);
    const [showAddPage,setShowAddPage]=useState(false);

    return (
        <div className="body">
            <div className="add" onClick={()=>{setShowAddPage(true)}}>新增产品</div>
            {showAddPage ? {AddPage}:null}
            {productModal}
        </div>
    )
}

export const AddPage = () => {

    return (
        <div className="addPage">
            <div className="head">Add product</div>
            <div className="textBlock">
                <p>avatar url : <input type="text"/></p>
                <p>product name : <input type="text"/></p>
                <p>desc : <input type="text"/></p>
            </div>
            <div className="bottom">
                <button onClick={() => setShowAddPage(false)}>Cancel</button>
                <button>Ok</button>
            </div>
        </div>
    )
}
