import React, { useState } from "react";
import {Product} from "./product";
import productData from "./productData";
//import {render} from "react-dom";
//import {AddPage} from "./addPage";


export const ProductShowPage = () => {
    const [showAddPage, setShowAddPage] = useState(false);
    const [product,setProduct] = useState(productData);

    const [input,setInput] = useState({url:"",name:"",introduction:""})
    const [url,setUrl] = useState("");
    const [name,setName] = useState("");
    const [introduction,setIntroduction] = useState("");
    const handleClick = () =>{
        console.log(input.url)
        console.log(name)
        console.log(introduction)
        setProduct(current =>[...current,{id:`${productData.length+1}`,url ,name ,introduction}])

    }
    const edit = () =>{

    }
    const deleteProduct = () =>{

    }

    const productModal = product.map(
        item => {
            return (

                <Product
                    key={item.id}
                    list={item}
                    edit={edit}
                    deleteProduct={deleteProduct}
                />


            )
        }
    )


    // const  handleSubmit = (e) => {
    //     e.preventDefault();
    //     const { username, password } = this;
    //     alert('avatar url ：' + url + 'product name ：' + name + 'desc :' + introduction)
    // }
    const saveUrl =(e)=>{
        setInput(e.target.input.value)
        console.log(e.target.value)
    }
    const saveName =(e)=>{
        setName(e.target.value)
    }
    const saveIntroduction =(e)=>{
        setIntroduction(e.target.value)
    }
    return (
        <div className="body">
            <div className="add" onClick={() => {
                setShowAddPage(true)
            }}>新增产品
            </div>
            {showAddPage ?
                <div className="addPage">
                    <div className="head">Add product</div>
                    {/*<form onSubmit={handleSubmit}>*/}
                        <div className="textBlock">
                            <p>avatar url : <input
                                type="text"
                                name="url"
                                onChange={saveUrl}
                            /></p>
                            <p>product name : <input
                                type="text"
                                name="name"
                                onChange={saveName}
                            />
                            </p>
                            <p>desc : <input
                                type="text"
                                name="introduction"
                                onChange={saveIntroduction}
                            /></p>
                        </div>
                        <div className="bottom">
                            <button onClick={() => setShowAddPage(false)}>Cancel</button>
                            <button
                                onClick={() => {
                                    setShowAddPage(false);
                                    handleClick();
                                }}>Ok
                            </button>
                        </div>
                    {/*</form>*/}
                </div>
                : null}
            {productModal}
        </div>
    )
}
