import React, { useState } from "react";
import {Product} from "./product";
import productData from "./productData";
//import {AddPage} from "./addPage";

export const Body = () =>{
    const productModal = productData.map(
        item => {
            return (

                <Product
                    key = {item.id}
                    list = {item}
                />


            )
        }
    )
    const [showAddPage,setShowAddPage]=useState(false);

    return (
        <div className="body">
            <div className="add" onClick={()=>{setShowAddPage(true)}}>新增产品</div>
            {showAddPage ?
                <div className="addPage">
                    <div className="head">Add product</div>
                    <form onSubmit={this.handleSubmit}>
                    <div className="textBlock">
                        <p>avatar url : <input type="text"value={this.state.value} onChange={this.handleChange}/></p>
                        <p>product name : <input type="text"value={this.state.value} onChange={this.handleChange}/></p>
                        <p>desc : <input type="text"value={this.state.value} onChange={this.handleChange}/></p>
                    </div>
                    <div className="bottom">
                        <button onClick={() => setShowAddPage(false)}>Cancel</button>
                        <button onClick={() => {
                            setShowAddPage(false)
                        }}>Ok</button>
                    </div>
                    </form>
                </div>
                :null}
            {productModal}
        </div>
    )
}
