import React, {useState} from "react";

export const AddPage = () =>{
    const [visiable,setvisiable] = useState(false);
    const [form] = Form.useForm();
    const open = () => {
        setVisiable(true);
    }
        const close = () => {
            setVisiable(false);
        };

        const submit = ()=>{
            form.submit()
        }

        const onSubmit = (values) =>{
            console.log(values)
            form.resetFields();
            close()
        }

    return(
        <div>
            <div className="text-center">
                <Button type="primary" onClick={open}>
                    新建
                </Button>
            </div>
            <Modal
                wrapClassName="modal-wrap"
                okText="提交"
                cancelButtonProps={{ shape: 'round' }}
                okButtonProps={{ shape: 'round' }}
                width={600}
                visible={visiable}
                title="新建用户"
                onCancel={close}
                onOk={submit}
            >
                <div className="form">
                    <Form form={form} labelCol={{ span: 4 }} wrapperCol={{ span: 16 }} onFinish={onSubmit}>
                        <Form.Item
                            label="用户名"
                            name="username"
                            rules={[{ required: true, message: 'Please input  username!' }]}
                        >
                            <Input />
                        </Form.Item>
                        <Form.Item
                            label="用户邮箱"
                            name="mail"
                            rules={[{ required: true, message: 'Please input mail!' }]}
                        >
                            <Input />
                        </Form.Item>
                        <Form.Item
                            label="部门"
                            name="depart"
                            rules={[{ required: true, message: 'Please input depart!' }]}
                        >
                            <Select>
                                <Select.Option value={1}>市场部</Select.Option>
                                <Select.Option value={2}>财务部</Select.Option>
                                <Select.Option value={3}>研发部</Select.Option>
                            </Select>
                        </Form.Item>
                    </Form>
                </div>
            </Modal>
        </div>


    )
}
